	If $$$ISERR(sc) $$$ThrowStatus(sc)
	Set tResult = tStatement.%Execute(Names)
	If (tResult.%SQLCODE'=0)&&(tResult.%SQLCODE'=100) $$$ThrowStatus($$$ERROR($$$SQLError, tResult.%SQLCODE, tResult.%Message)) 
